@charset "UTF-8";
/* Footer Styles */
.disaster-relief-footer {
  background-color: #b91c1c; /* Nền đỏ */
  color: #ffffff; /* Màu chữ trắng */
  padding: 20px 0;
  font-family: Arial, sans-serif;
}

.footer-container {
  display: flex;
  justify-content: space-between;
  flex-wrap: wrap;
  max-width: 1200px;
  margin: 0 auto;
  padding: 20px;
}

.footer-about, .footer-contact, .footer-social {
  flex: 1 1 calc(33.333% - 40px);
  margin: 10px;
}

.footer-about h3 {
  font-size: 20px;
  margin-bottom: 10px;
  color: #f0f0f0; /* Màu trắng nhạt */
}

.footer-about p {
  font-size: 14px;
  line-height: 1.6;
  color: #ffffff; /* Màu chữ trắng */
}

.footer-contact h4, .footer-social h4 {
  font-size: 18px;
  margin-bottom: 10px;
  color: #f5f5f5; /* Màu trắng nhạt */
}

.footer-contact p, .footer-social a {
  font-size: 14px;
  line-height: 1.6;
  color: #ffffff; /* Màu chữ trắng */
  text-decoration: none;
}

.footer-social a {
  display: block;
  margin: 5px 0;
  transition: color 0.3s ease;
}

.footer-social a:hover {
  color: #f0f0f0; /* Màu trắng nhạt hơn khi hover */
}

.footer-bottom {
  text-align: center;
  background-color: #991b1b; /* Nền đỏ đậm hơn một chút */
  padding: 10px 0;
  font-size: 14px;
  color: #ffffff; /* Màu chữ trắng */
  margin-top: 20px;
}

